const sliders=document.querySelectorAll(".slider-item"),sliderToggles=document.querySelectorAll(".slider-toggle"),bgClasses={0:"bg1",1:"bg2",2:"bg3"},bgClass=new RegExp("bg\\d"),changeSlide=e=>{const s=Array.from(sliders).find(e=>e.classList.contains("slider-current")),r=Array.from(sliderToggles).find(e=>e.classList.contains("slider-toggle-current"));var l=Array.from(sliderToggles).indexOf(e);s.classList.remove("slider-current"),r.classList.remove("slider-toggle-current"),sliders[l].classList.add("slider-current"),sliderToggles[l].classList.add("slider-toggle-current");e=document.body.className.match(bgClass)[0],l=bgClasses[l];document.body.classList.replace(e,l)};sliderToggles.forEach(e=>{e.addEventListener("click",e=>{e.preventDefault();e=e.target;changeSlide(e)})});
